import { Component, Input, OnInit } from '@angular/core';
import { faUsers } from '@fortawesome/free-solid-svg-icons';
import { User } from '../authentification/user';
import { Router } from '@angular/router';

@Component({
  selector: 'app-create-vaccination-center',
  templateUrl: './create-vaccination-center.component.html',
  styleUrls: ['./create-vaccination-center.component.scss']
})
export class CreateVaccinationCenterComponent implements OnInit{

  usersIcon = faUsers;
  @Input() user?: User;

  constructor(private router: Router) {}

  ngOnInit(): void {

  }

  CancelCreation(){
    this.router.navigate(['userdashboard', this.user?.id]);
  }

}
