import { Component, OnInit } from '@angular/core';
import { User } from '../authentification/user';
import { ActivatedRoute } from '@angular/router';
import { UserService } from '../user.service';

@Component({
  selector: 'app-update-superadmin',
  templateUrl: './update-superadmin.component.html',
  styleUrls: ['./update-superadmin.component.scss']
})
export class UpdateSuperadminComponent implements OnInit {

  superAdmin?: User;

  superAdminUpdated = false;

  constructor(private route: ActivatedRoute, private userService: UserService){}

  ngOnInit(): void {
      const id = Number(this.route.snapshot.paramMap.get("id"));
      this.userService.getUserById(id).subscribe(resultSuperAdmin=>{
        this.superAdmin = resultSuperAdmin;
      })
  }

  updateSuperAdmin(){
    if(this.superAdmin){

    }
  }

}
<div class="component" *ngIf="superAdmin">
    <form *ngIf="!superAdminUpdated">
        <div class="labelinput">
            <label for="fname"> Nom </label>
            <input id="fname" name="fname" [(ngModel)]="superAdmin.fname"/>
        </div> 
        <div class="labelinput">
            <label for="lname"> Téléphone </label>
            <input id="lname" name="lname" [(ngModel)]="superAdmin.lname"/>
        </div>
        <div class="labelinput">
            <label for="address"> Adresse </label>
            <input id="address" name="address" [(ngModel)]="center.address"/>
        </div>
        <div class="citypostal">
            <div class="labelinput">
                <label for="city"> Ville </label>
                <input id="city" name="city" [(ngModel)]="center.city"/>
            </div>
            <div class="labelinput">
                <label for="postal"> Code Postal </label>
                <input id="postal" name="postal" [(ngModel)]="center.postal"/>
            </div>
        </div>
            <div class="cancelupdate">
                <button routerLink="../../sadmincenters"> Annuler </button>
                <button (click)="updateCenter()"> Valider </button>
            </div>
    </form>

    <div id="updated" *ngIf="centerUpdated">
        Les informations du centre {{center.name}} ont bien été mises à jour
    </div>
</div>
