import { Component, OnInit } from '@angular/core';
import { faPlusCircle, faXmarkCircle, faPen } from '@fortawesome/free-solid-svg-icons';
import { User } from '../authentification/user';
import { VaccinationCenter } from '../vaccination-center-list/vaccination-center';
import { ActivatedRoute } from '@angular/router';
import { UserService } from '../user.service';
import { VaccinationService } from '../vaccination.service';
import { Location } from '@angular/common';

@Component({
  selector: 'app-mycenter-dashboard',
  templateUrl: './mycenter-dashboard.component.html',
  styleUrls: ['./mycenter-dashboard.component.scss']
})
export class MycenterDashboardComponent implements OnInit{

    admin?: User;
    doctors?: User[];
    center?: VaccinationCenter;
    selected?: User;
    showModal: boolean = false;

    plusIcon = faPlusCircle;
    xIcon = faXmarkCircle;
    modifyIcon = faPen;

    constructor(private route: ActivatedRoute, private location:Location, private userService: UserService, private vaccinationService: VaccinationService){}

    ngOnInit(): void {
        const id = Number(this.route.snapshot.paramMap.get("id"));
        this.userService.getUserById(id).subscribe(resultAdmin=>{
          this.admin = resultAdmin;
        })
        this.userService.getDoctorsByAdminId(id).subscribe(resultDoctors=>{
          this.doctors = resultDoctors;
        });
        this.vaccinationService.getAdminCenter(id).subscribe(resultCenter=>{
          this.center = resultCenter;
        })

    }

    deleteDoctor(aDoctor: User){
      this.selected = aDoctor;
      this.showModal = true;
    }

    cancelDoctorDelete(){
      this.showModal = false;
    }

    confirmDoctorDelete(){
      this.userService.deleteDoctor(this.selected!.id).subscribe();
      this.doctors?.splice(this.doctors.indexOf(this.selected!), 1);
      this.showModal=false
    }

    cancelDoctorCreation(){
      this.location.back();
    }
}
<div class="component" *ngIf="center">
    <div class="details">
        <span> {{center.name}}</span><br/>
        <span> {{center.address}} {{center.postal}}, {{center.city}} </span>
    </div>

    <div *ngIf="showModal" class="overlay">
        <div class="confirm">
            <p>Êtes-vous sûr de vouloir supprimer ce Docteur ?</p>
            <div class="confbuttons">
                <button (click)="confirmDoctorDelete()">Confirmer</button>
                <button (click)="cancelDoctorDelete()">Annuler</button>
            </div>
        </div>
    </div>

    <table>
        <tr> 
            <td colspan="3"> Docteurs <button routerLink="../../createdoctor/{{center.id}}"><fa-icon [icon]="plusIcon"></fa-icon></button></td> 
        </tr>
        <tr *ngFor="let aDoctor of doctors"> 
            <td>{{aDoctor.id}}</td> 
            <td>{{aDoctor.fname}} {{aDoctor.lname}} </td>
            <!-- changer le routerLink qui n'est pas bon-->
            <td id="lastcol"> <div class="buttons"><button (click)="cancelDoctorCreation()" ><fa-icon [icon]="modifyIcon"></fa-icon></button> <button (click)="deleteDoctor(aDoctor)"><fa-icon [icon]="xIcon"></fa-icon></button> </div></td>
        </tr>
    </table>
</div>
