import { Component, OnInit } from '@angular/core';
import { UserService } from '../user.service';
import { User } from '../authentification/user';
import { faPlusCircle } from '@fortawesome/free-solid-svg-icons';

@Component({
  selector: 'app-configuration',
  templateUrl: './configuration.component.html',
  styleUrls: ['./configuration.component.scss']
})
export class ConfigurationComponent implements OnInit {

  superadmins!: User[];

  plusIcon = faPlusCircle;

  constructor(private service: UserService){}

  ngOnInit(): void {
      this.service.getSuperAdmins().subscribe(resultSuperAdmins=>{
        this.superadmins = resultSuperAdmins;
      })
  }
}
<div class="component">
    <table>
        <tr> <td colspan="2">Super Administrateur <button><fa-icon [icon]="plusIcon"></fa-icon></button></td> </tr>
        <tr *ngFor="let aSuperAdmin of superadmins"> <td>{{aSuperAdmin.id}}</td> <td>{{aSuperAdmin.fname}} {{aSuperAdmin.lname}} </td></tr>
    </table>
</div>
