import { Component, OnInit } from '@angular/core';
import { User } from '../authentification/user';
import { ActivatedRoute } from '@angular/router';
import { UserService } from '../user.service';

@Component({
  selector: 'app-update-superadmin',
  templateUrl: './update-superadmin.component.html',
  styleUrls: ['./update-superadmin.component.scss']
})
export class UpdateSuperadminComponent implements OnInit {

  superAdmin?: User;

  superAdminUpdated = false;

  constructor(private route: ActivatedRoute, private userService: UserService){}

  ngOnInit(): void {
      const id = Number(this.route.snapshot.paramMap.get("id"));
      this.userService.getUserById(id).subscribe(resultSuperAdmin=>{
        this.superAdmin = resultSuperAdmin;
      })
  }

  updateSuperAdmin(){
    if(this.superAdmin){

    }
  }

}
<div class="component" *ngIf="superAdmin">
    <form *ngIf="!superAdminUpdated">
        <div class="name">
            <div class="labelinput">
                <label for="fname"> Prénom </label>
                <input id="fname" name="fname" [(ngModel)]="superAdmin.fname"/>
            </div> 
            <div class="labelinput">
                <label for="lname"> Nom </label>
                <input id="lname" name="lname" [(ngModel)]="superAdmin.lname"/>
            </div>
        </div>
        <div class="labelinput">
            <label for="mail"> Email </label>
            <input id="mail" name="mail" [(ngModel)]="superAdmin.mail"/>
        </div>
        <div class="labelinput">
            <label for="password"> Mot de passe </label>
            <input type="password" id="password" name="password" [(ngModel)]="superAdmin.password"/>            </div>
        <div class="labelinput">
            <label for="phone"> Téléphone </label>
            <input id="phone" name="phone" [(ngModel)]="superAdmin.phone"/>
        </div>
        <div class="cancelupdate">
            <button routerLink="../../configuration"> Annuler </button>
            <button (click)="updateSuperAdmin()"> Valider </button>
        </div>
    </form>

    <div id="updated" *ngIf="superAdminUpdated">
        Les informations du Super Administrateur {{superAdmin.fname}} {{superAdmin.lname}} ont bien été mises à jour
    </div>
</div>
