import { Component } from '@angular/core';
import { VaccinationCenter } from '../vaccination-center/vaccination-center';

@Component({
  selector: 'app-vaccination-center-list',
  templateUrl: './vaccination-center-list.component.html',
  styleUrls: ['./vaccination-center-list.component.scss']
})
export class VaccinationCenterListComponent {

  centers: VaccinationCenter[] = [
    {id: 1, name: "Hopital Central", address: "Rue Lionnois", postalCode: "54000", city: "Nancy"},
    {id: 2, name: "Grand centre de vaccination", address: "Rue Picasso", postalCode: "54300", city: "Lunéville"},
    {id: 1, name: "Salle des fêtes", address: "Rue Machin", postalCode: "54110", city: "Thionville"}
  ];

  selected?: VaccinationCenter;

  constructor() {}

  isSpecialCenter(center: VaccinationCenter){
    return center.city == "Nancy";
  }

  selectCenter(center: VaccinationCenter){
    this.selected=center;
  }


}
