import { Component, OnInit } from '@angular/core';
import { UserService } from '../user.service';
import { User } from '../authentification/user';
import { faPlusCircle, faXmarkCircle, faPen } from '@fortawesome/free-solid-svg-icons';

@Component({
  selector: 'app-configuration',
  templateUrl: './configuration.component.html',
  styleUrls: ['./configuration.component.scss']
})
export class ConfigurationComponent implements OnInit {

  superadmins!: User[];
  showModal: boolean = false;

  plusIcon = faPlusCircle;
  xIcon = faXmarkCircle;
  modifyIcon = faPen;

  constructor(private service: UserService){}

  ngOnInit(): void {
      this.service.getSuperAdmins().subscribe(resultSuperAdmins=>{
        this.superadmins = resultSuperAdmins;
      })
  }

  deleteSuperAdmin(){

  }
}
<div class="component">
    <table>
        <tr> 
            <td colspan="3">Super Administrateur <button><fa-icon [icon]="plusIcon"></fa-icon></button></td> 
        </tr>
        <tr *ngFor="let aSuperAdmin of superadmins"> 
            <td>{{aSuperAdmin.id}}</td> 
            <td>{{aSuperAdmin.fname}} {{aSuperAdmin.lname}} </td>
            <td id="lastcol"> <div class="buttons"><button><fa-icon [icon]="modifyIcon"></fa-icon></button> <button (click)="deleteSuperAdmin()"><fa-icon [icon]="xIcon"></fa-icon></button> </div></td>
        </tr>
    </table>
</div>
