import { Component, OnInit } from '@angular/core';
import { UserService } from '../user.service';
import { User } from '../authentification/user';
import { faPlusCircle, faXmarkCircle, faPen } from '@fortawesome/free-solid-svg-icons';
import { Router } from '@angular/router';

@Component({
  selector: 'app-configuration',
  templateUrl: './configuration.component.html',
  styleUrls: ['./configuration.component.scss']
})
export class ConfigurationComponent implements OnInit {

  superadmins!: User[];
  showModal: boolean = false;
  selected?: User;

  plusIcon = faPlusCircle;
  xIcon = faXmarkCircle;
  modifyIcon = faPen;

  constructor(private service: UserService){}

  ngOnInit(): void {
      this.service.getSuperAdmins().subscribe(resultSuperAdmins=>{
        this.superadmins = resultSuperAdmins;
      })
  }

  deleteSuperAdmin(aSuperAdmin: User){
    this.selected = aSuperAdmin;
    this.showModal = true;
  }

  confirmDelete(){
    this.service.deleteSuperAdmin(this.selected!.id).subscribe();
    this.superadmins.splice(this.superadmins.indexOf(this.selected!), 1)
    this.showModal = false;
  }

  cancelDelete(){
    this.showModal = false;
  }

}
<div class="component">

    <div *ngIf="showModal" class="overlay">
        <div class="confirm">
            <p>Êtes-vous sûr de vouloir supprimer ce Super Administrateur ?</p>
            <div class="confbuttons">
                <button (click)="confirmDelete()">Confirmer</button>
                <button (click)="cancelDelete()">Annuler</button>
            </div>
        </div>
    </div>

    <table>
        <tr> 
            <td colspan="3">Super Administrateur <button routerLink="../createsuperadmin"><fa-icon [icon]="plusIcon"></fa-icon></button></td> 
        </tr>
        <tr *ngFor="let aSuperAdmin of superadmins"> 
            <td>{{aSuperAdmin.id}}</td> 
            <td>{{aSuperAdmin.fname}} {{aSuperAdmin.lname}} </td>
            <td id="lastcol"> <div class="buttons"><button routerLink="../updatesuperadmin/{{aSuperAdmin.id}}"><fa-icon [icon]="modifyIcon"></fa-icon></button> <button (click)="deleteSuperAdmin(aSuperAdmin)"><fa-icon [icon]="xIcon"></fa-icon></button> </div></td>
        </tr>
    </table>
</div>
