import { Component, OnInit } from '@angular/core';
import { faUsers } from '@fortawesome/free-solid-svg-icons';
import { VaccinationCenter } from '../vaccination-center-list/vaccination-center';
import { VaccinationService } from '../vaccination.service';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-update-vaccination-center',
  templateUrl: './update-vaccination-center.component.html',
  styleUrls: ['./update-vaccination-center.component.scss']
})
export class UpdateVaccinationCenterComponent implements OnInit {
  usersIcon = faUsers;

  center?: VaccinationCenter;

  centerUpdated = false;

  constructor(private route: ActivatedRoute, private vaccinationService: VaccinationService){}

  ngOnInit(): void {
      const id = Number(this.route.snapshot.paramMap.get("id"));
      this.vaccinationService.getCenterById(id).subscribe(resultCenter=>{
        this.center = resultCenter;
      })
  }

    updateCenter(){

  }
}
<div class="component" *ngIf="center">
    <form *ngIf="!centerUpdated">
        <div class="labelinput">
            <label for="name"> Nom </label>
            <input id="name" name="name" [(ngModel)]="center.name"/>
        </div> 
        <div class="labelinput">
            <label for="phone"> Téléphone </label>
            <input id="phone" name="phone" [(ngModel)]="center.phone"/>
        </div>
        <div class="labelinput">
            <label for="address"> Adresse </label>
            <input id="address" name="address" [(ngModel)]="center.address"/>
        </div>
        <div class="citypostal">
            <div class="labelinput">
                <label for="city"> Ville </label>
                <input id="city" name="city" [(ngModel)]="center.city"/>
            </div>
            <div class="labelinput">
                <label for="postal"> Code Postal </label>
                <input id="postal" name="postal" [(ngModel)]="center.postal"/>
            </div>
        </div>
        <div class="buttons">
            <button id="usersicon"> <fa-icon [icon]="usersIcon"></fa-icon> </button>
            <div class="cancelupdate">
                <button routerLink="../sadmincenters"> Annuler </button>
                <button (click)="updateCenter"> Valider </button>
            </div>
        </div>
    </form>

    <div id="created" *ngIf="centerUpdated">
        Le centre {{center.name}} a bien été créé
    </div>
</div>
