import { Component } from '@angular/core';
import { VaccinationCenter } from '../vaccination-center/vaccination-center';
import { VaccinationService } from '../vaccination.service';

@Component({
  selector: 'app-vaccination-center-list',
  templateUrl: './vaccination-center-list.component.html',
  styleUrls: ['./vaccination-center-list.component.scss']
})
export class VaccinationCenterListComponent {

  centers: VaccinationCenter[] = [
    {id: 1, name: "Hopital Central", address: "Rue Lionnois", postalCode: "54000", city: "Nancy", openingDate: new Date("2022-10-05")},
    {id: 2, name: "Grand centre de vaccination", address: "Rue Picasso", postalCode: "54300", city: "Lunéville", openingDate: new Date("2021-02-27")},
    {id: 3, name: "Salle des fêtes", address: "Rue Machin", postalCode: "54110", city: "Thionville", openingDate: new Date("2021-09-16")}
  ];

  selected?: VaccinationCenter;

  constructor(private service: VaccinationService) {}

  ngOnInit(): void {
    this.centers = this.service.getAllVaccinationCenter();
  }

  isSpecialCenter(center: VaccinationCenter){
    return center.city == "Nancy";
  }

  selectCenter(center: VaccinationCenter){
    this.selected=center;
  }

  onDeleted(center: VaccinationCenter){
    delete this.selected;
    this.centers.splice(this.centers.indexOf(center), 1);
  }


}
<h3> Liste des centres de vaccination</h3>
<ul>
    <li *ngFor="let aCenter of centers" 
        [ngClass]="selected==aCenter ? 'selected' : ''">
        <button
            [ngStyle]="{'font-weight' : isSpecialCenter(aCenter) ? 'bold' : 'normal'}"
            (click)=" selectCenter(aCenter)">

            {{aCenter.name}}

        </button>
    </li>
</ul>

<app-vaccination-center [center]="selected" (deleted)="onDeleted($event)"></app-vaccination-center>
