import { Component, OnInit } from '@angular/core';
import { VaccinationCenter } from '../vaccination-center-list/vaccination-center';
import { ActivatedRoute } from '@angular/router';
import { VaccinationService } from '../vaccination.service';
import { UserService } from '../user.service';
import { User } from '../authentification/user';
import { faMagnifyingGlass } from '@fortawesome/free-solid-svg-icons';

@Component({
  selector: 'app-planning',
  templateUrl: './planning.component.html',
  styleUrls: ['./planning.component.scss']
})
export class PlanningComponent implements OnInit{

  center?: VaccinationCenter;
  user?: User;
  patient_lname?: string;
  searchIcon = faMagnifyingGlass;

  constructor(private route: ActivatedRoute, private vaccinationService: VaccinationService, private userService: UserService){}

  ngOnInit(): void{
    const id = Number(this.route.snapshot.paramMap.get("id"));
    this.userService.getUserById(id).subscribe(resultUser=>{
      this.user = resultUser;

      if(this.user.role == 'admin'){
        this.vaccinationService.getAdminCenter(this.user.id).subscribe(resultCenter=>{
          this.center = resultCenter;
        })
      }

      if(this.user.role == 'doctor'){
        this.vaccinationService.getDoctorCenter(this.user.id).subscribe(resultCenter=>{
          this.center = resultCenter;
        })
      }
    })

  }

}
<div class="component" *ngIf="center">
    <div class="details">
        <span> {{center.name}}</span><br/>
        <span> {{center.address}} {{center.postal}}, {{center.city}} </span>
    </div>
    <div class="searchcenters">
        <input [(ngModel)]="patient_lname" placeholder="Nom de famille du patient"/>
        <button><fa-icon [icon]="searchIcon"> </fa-icon></button>
    </div>
</div>
