import { Component, OnInit } from '@angular/core';
import { VaccinationCenter } from './vaccination-center';

@Component({
  selector: 'app-vaccination-center',
  templateUrl: './vaccination-center.component.html',
  styleUrls: ['./vaccination-center.component.scss']
})
export class VaccinationCenterComponent implements OnInit {

  center: VaccinationCenter = {
    id: 1,
    name: "Hôpital Central",
    address: "Rue Lionnois",
    postalCode: "54000",
    city: "Nancy",
    openingDate: new Date("2022-01-23")
  }

  constructor() {}

  ngOnInit(): void {

  }

  clearName(){
    this.center.name = "";
  }

}
<div class="detail" *ngIf="center">
    <h2> {{center.name | uppercase}} </h2>
    <div> {{center.address}} 
         <span>{{center.postalCode}} {{center.city}} </span>
    </div>
    <p> Ouvert depuis le {{center.openingDate | date: 'dd:MM:Y'}}</p>
 </div>
