import { Component, OnInit, Input, Output, EventEmitter } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { VaccinationService } from '../vaccination.service';
import { Booking } from './booking';
import { VaccinationCenter } from '../vaccination-center-list/vaccination-center';
import { DatePipe } from '@angular/common';

@Component({
  selector: 'app-vaccination-center',
  templateUrl: './vaccination-center.component.html',
  styleUrls: ['./vaccination-center.component.scss'],
  providers: [DatePipe]
})
export class VaccinationCenterComponent implements OnInit {

  @Input() center?: VaccinationCenter;
  @Output() deleted = new EventEmitter<VaccinationCenter>();

  booking: Booking = {
    id: 0,
    fname: '',
    lname: '',
    mail: '',
    phone: '',
    registration_date: ''
  };

  emptyfname?: boolean;
  emptylname?: boolean;
  emptymail?: boolean;
  emptyphone?: boolean;
  emptydate?: boolean;
  emptymsg?: string;

  constructor(private route: ActivatedRoute, private vaccinationService: VaccinationService, private router: Router, private datePipe: DatePipe) {}

  ngOnInit(): void {
      const id = Number(this.route.snapshot.paramMap.get("id"));
      this.vaccinationService.getCenterById(id).subscribe(resultCenter=>{
        this.center = resultCenter;
      })
  }

  clearName(){
    if(this.center){
      this.center.name = "";
    }
  }

  delete(){
    this.deleted.emit(this.center);
  }

  formatDate(): string {
    return this.datePipe.transform(this.booking.registration_date, 'dd/MM/yyyy');
  }

  saveBooking(){
    if(this.center){
      if(this.booking.fname != "" && this.booking.lname != "" && this.booking.mail != "" && this.booking.phone != "" && this.booking.registration_date != ""){
        this.vaccinationService.createBooking(this.center.id, this.booking).subscribe();
        this.router.navigate(['registered'])
      }

      else{
        if(this.booking.fname == ""){
          this.emptyfname = true;
        }
        else{
          this.emptyfname = false;
        }

        if(this.booking.lname == ""){
          this.emptylname = true;
        }
        else{
          this.emptylname = false;
        }

        if(this.booking.mail == ""){
          this.emptymail = true;
        }
        else{
          this.emptymail = false;
        }

        if(this.booking.phone == ""){
          this.emptyphone = true;
        }
        else{
          this.emptyphone = false;
        }

        if(this.booking.registration_date == ""){
          this.emptydate = true;
        }
        else{
          this.emptydate = false;
        }
        this.emptymsg = "Veuillez remplir tous les champs pour finaliser votre enregistrement";
      }
    }
  }

}
<div class="reservation" *ngIf="center">
    <div class="details">
        <span> {{center.name}}</span> <br/>
        <span> {{center.address}}, {{center.postal}} {{center.city}} 
        </span> <br/>
        <span> {{center.phone}} </span>
    </div>

    <form>
    <div class="formreservation">
        <div id="fnamelname">
        <div class="labelinput">
            <label for="fname"> Prénom </label>
            <input id="fname" name="fname" [(ngModel)]="booking.fname" [ngClass]="emptyfname? 'empty' : ''"/>
        </div>
        <div class="labelinput" id="lnamegroup">
            <label for="lname"> Nom </label>
            <input id="lname" name="lname" [(ngModel)] = "booking.lname" [ngClass]="emptylname? 'empty' : ''"/>
        </div>
        </div>

        <div class="labelinput">
            <label for="email"> Email </label>
            <input id="email" name="email" [(ngModel)] = "booking.mail" [ngClass]="emptymail? 'empty' : ''"/>
        </div>
        <div class="labelinput">
            <label for="phone"> Téléphone </label>
            <input id="phone" name="phone" [(ngModel)] = "booking.phone" [ngClass]="emptyphone? 'empty' : ''"/>
        </div>
        <div class="button">

        <div class="labelinput">
            <label for="date"> Date du rendez-vous </label>
            <input type="date" id="date" name="date" [(ngModel)] = "booking.registration_date" [ngClass]="emptydate? 'empty' : ''" />
        </div>
            <button (click)="saveBooking()">Réserver</button>
        </div>
        <span> {{emptymsg}} </span>
    </div>
    </form>

    <!-- <div>
        <button (click)="clearName()"> Clear Name</button>
        <button id="delete" (click)="delete()"> Supprimer </button>
    </div> -->

</div>
