        </tr>
    </table>
    <table>
        <tr> 
            <td colspan="3"> Docteurs <button><fa-icon [icon]="plusIcon"></fa-icon></button></td> 
        </tr>
        <tr *ngFor="let aDoctor of doctors"> 
            <td>{{aDoctor.id}}</td> 
