import { Component, OnInit, Input, Output, EventEmitter } from '@angular/core';
import { VaccinationCenter } from './vaccination-center';
import { ActivatedRoute } from '@angular/router';
import { VaccinationService } from '../vaccination.service';
import { Booking } from '../booking';

@Component({
  selector: 'app-vaccination-center',
  templateUrl: './vaccination-center.component.html',
  styleUrls: ['./vaccination-center.component.scss']
})
export class VaccinationCenterComponent implements OnInit {

  @Input() center?: VaccinationCenter;
  @Output() deleted = new EventEmitter<VaccinationCenter>();

  book!: Booking;

  constructor(private route: ActivatedRoute, private vaccinationService: VaccinationService) {}

  ngOnInit(): void {
      const id = Number(this.route.snapshot.paramMap.get("id"));
      this.vaccinationService.getCenterById(id).subscribe(resultCenter=>{
        this.center = resultCenter;
      })
  }

  clearName(){
    if(this.center){
      this.center.name = "";
    }
  }

  delete(){
    this.deleted.emit(this.center);
  }

}
<div class="reservation" *ngIf="center">
    <div class="details">
        <span> {{center.name}}</span> <br/>
        <span> {{center.address}}, {{center.postal}} {{center.city}} 
        </span> <br/>
        <span> {{center.phone}} </span>
    </div>
    <div class="formreservation">
        <div id="fnamelname">
        <div class="labelinput">
            <label for="fname"> Prénom </label>
            <input type="text" id="fname"[(ngModel)] = "book.fname"/>
        </div>
        <div class="labelinput" id="lnamegroup">
            <label for="lname"> Nom </label>
            <input type="text" id="lname"/>
        </div>
        </div>

        <div class="labelinput">
            <label for="email"> Email </label>
            <input type="text" id="email"/>
        </div>
        <div class="labelinput">
            <label for="phone"> Téléphone </label>
            <input type="text" id="phone"/>
        </div>
        <div class="labelinput">
            <label for="date"> Date du rendez-vous </label>
            <input type="date" id="date"/>
        </div>
            <button (click)=" saveBooking()">Réserver</button>
    </div>

    <!-- <div>
        <button (click)="clearName()"> Clear Name</button>
        <button id="delete" (click)="delete()"> Supprimer </button>
    </div> -->

</div>
