import { Component, OnInit } from '@angular/core';
import { VaccinationCenter } from '../vaccination-center-list/vaccination-center';
import { User } from '../authentification/user';
import { ActivatedRoute } from '@angular/router';
import { UserService } from '../user.service';
import { VaccinationService } from '../vaccination.service';

@Component({
  selector: 'app-create-doctor',
  templateUrl: './create-doctor.component.html',
  styleUrls: ['./create-doctor.component.scss']
})
export class CreateDoctorComponent implements OnInit{

  center!: VaccinationCenter;
  selectedCenter?: VaccinationCenter;
  centers!: VaccinationCenter[];
  doctor: User = {
    id: 0,
    fname: '',
    lname: '',
    mail: '',
    password: '',
    phone: '',
    role:'doctor'
  }

  emptyfname?: boolean;
  emptylname?: boolean;
  emptymail?: boolean;
  emptypassword?: boolean;
  emptyphone?: boolean;
  emptycenter?: boolean;
  emptymsg?: string;
  DoctorCreated = false;

  constructor(private route:ActivatedRoute, private userService: UserService, private centerService: VaccinationService){}

  ngOnInit(): void {
      const id = Number(this.route.snapshot.paramMap.get("centerid"));
      this.centerService.getCenterById(id).subscribe(resultCenter=>{
        this.center = resultCenter;
      })

      this.centerService.getAllVaccinationCenter().subscribe(resultCenters=>{
        this.centers = resultCenters;
      })

  }

  createDoctor(){
    if(this.doctor.fname != "" && this.doctor.lname != "" && this.doctor.mail != "" && this.doctor.password != "" && this.doctor.phone != "" && this.selectedCenter != undefined){
      this.userService.createDoctor(this.selectedCenter.id, this.doctor).subscribe();
      this.DoctorCreated = true;
    }

    else{
      if(this.doctor.fname == ""){
        this.emptyfname = true;
      }
      else{
        this.emptyfname = false;
      }

      if(this.doctor.lname == ""){
        this.emptylname = true;
      }
      else{
        this.emptylname = false;
      }

      if(this.doctor.mail == ""){
        this.emptymail = true;
      }
      else{
        this.emptymail = false;
      }

      if(this.doctor.password == ""){
        this.emptypassword = true;
      }
      else{
        this.emptypassword = false;
      }

      if(this.doctor.phone == ""){
        this.emptyphone = true;
      }
      else{
        this.emptyphone = false;
      }

      if(this.selectedCenter == undefined){
        this.emptycenter = true;
      }
      else{
        this.emptycenter = false;
      }
      this.emptymsg = "Veuillez remplir tous les champs pour créer le docteur";
    }
  }
}
<div class="component">
    <form *ngIf="!DoctorCreated">
        <div class="name">
            <div class="labelinput">
                <label for="fname"> Prénom </label>
                <input id="fname" name="fname" [(ngModel)]="doctor.fname" [ngClass]="emptyfname? 'empty' : '' "/>
            </div> 
            <div class="labelinput">
                <label for="lname"> Nom </label>
                <input id="lname" name="lname" [(ngModel)]="doctor.lname" [ngClass]="emptylname? 'empty' : '' "/>
            </div>
        </div>
        <div class="labelinput">
            <label for="mail"> Email </label>
            <input id="mail" name="mail" [(ngModel)]="doctor.mail" [ngClass]="emptymail? 'empty' : '' "/>
        </div>
        <div class="labelinput">
            <label for="password"> Mot de passe </label>
            <input type="password" id="password" name="password" [(ngModel)]="doctor.password" [ngClass]="emptypassword? 'empty' : '' "/>
        </div>
        <div class="labelinput">
            <label for="phone"> Téléphone </label>
            <input id="phone" name="phone" [(ngModel)]="doctor.phone" [ngClass]="emptyphone? 'empty' : '' "/>
        </div>
        <div class="labelselect">
            <label for="centre"> Centre de ratachement </label>
            <select name="centre" id="centre" [(ngModel)]="selectedCenter" [ngClass]="emptycenter? 'empty' : '' ">
                <option [ngValue]="undefined"> Choisissez le centre de ratachement</option>
                <option *ngFor="let aCenter of centers" [ngValue]="aCenter"> {{aCenter.name}} </option>
            </select>
        </div>
        <div class="buttons">
            <button (click)="cancelDoctorCreate()"> Annuler </button>
            <button (click)="createDoctor()"> Valider </button>
        </div>
        <span> {{emptymsg}} </span>
    </form>
    <div id="created" *ngIf="DoctorCreated">
        Le docteur {{doctor.fname}} {{doctor.lname}} a bien été créé
    </div>

</div>
