import { Component, OnInit } from '@angular/core';
import { User } from '../authentification/user';
import { UserService } from '../user.service';
import { ActivatedRoute } from '@angular/router';
import { VaccinationService } from '../vaccination.service';
import { VaccinationCenter } from '../vaccination-center-list/vaccination-center';

@Component({
  selector: 'app-create-admin',
  templateUrl: './create-admin.component.html',
  styleUrls: ['./create-admin.component.scss']
})
export class CreateAdminComponent implements OnInit{
  center!: VaccinationCenter;
  admin: User = {
    id: 0,
    fname: '',
    lname: '',
    mail: '',
    password: '',
    phone: '',
    role:'admin'
  }

  emptyfname?: boolean;
  emptylname?: boolean;
  emptymail?: boolean;
  emptypassword?: boolean;
  emptyphone?: boolean;
  emptymsg?: string;
  AdminCreated = false;

  constructor(private route:ActivatedRoute, private userService: UserService, private centerService: VaccinationService){}

  ngOnInit(): void {
      const id = Number(this.route.snapshot.paramMap.get("centerid"));
      this.centerService.getCenterById(id).subscribe(resultCenter=>{
        this.center = resultCenter;
      })

  }

  createAdmin(){
    if(this.admin.fname != "" && this.admin.lname != "" && this.admin.mail != "" && this.admin.password != "" && this.admin.phone != ""){
      this.userService.createAdmin(this.center.id, this.admin).subscribe();
      this.AdminCreated = true;
    }

    else{
      if(this.admin.fname == ""){
        this.emptyfname = true;
      }
      else{
        this.emptyfname = false;
      }

      if(this.admin.lname == ""){
        this.emptylname = true;
      }
      else{
        this.emptylname = false;
      }

      if(this.admin.mail == ""){
        this.emptymail = true;
      }
      else{
        this.emptymail = false;
      }

      if(this.admin.password == ""){
        this.emptypassword = true;
      }
      else{
        this.emptypassword = false;
      }

      if(this.admin.phone == ""){
        this.emptyphone = true;
      }
      else{
        this.emptyphone = false;
      }
      this.emptymsg = "Veuillez remplir tous les champs pour créer l'administrateur";
    }
  }
}
<div class="component">
    <form *ngIf="!AdminCreated">
        <div class="name">
            <div class="labelinput">
                <label for="fname"> Prénom </label>
                <input id="fname" name="fname" [(ngModel)]="admin.fname" [ngClass]="emptyfname? 'empty' : '' "/>
            </div> 
            <div class="labelinput">
                <label for="lname"> Nom </label>
                <input id="lname" name="lname" [(ngModel)]="admin.lname" [ngClass]="emptylname? 'empty' : '' "/>
            </div>
        </div>
        <div class="labelinput">
            <label for="mail"> Email </label>
            <input id="mail" name="mail" [(ngModel)]="admin.mail" [ngClass]="emptymail? 'empty' : '' "/>
        </div>
        <div class="labelinput">
            <label for="password"> Mot de passe </label>
            <input type="password" id="password" name="password" [(ngModel)]="admin.password" [ngClass]="emptypassword? 'empty' : '' "/>
        </div>
        <div class="labelinput">
            <label for="phone"> Téléphone </label>
            <input id="phone" name="phone" [(ngModel)]="admin.phone" [ngClass]="emptyphone? 'empty' : '' "/>
        </div>
        <div class="labelinput">
            <label for="centre"> Centre de ratachement </label>
            <select name="centre" id="centre">
                <option> Choisissez le centre de ratachement</option>
            </select>
        </div>
        <div class="buttons">
            <button routerLink="../../sadmincenters"> Annuler </button>
            <button (click)="createAdmin()"> Valider </button>
        </div>
        <span> {{emptymsg}} </span>
    </form>
    <div id="created" *ngIf="AdminCreated">
        L'administrateur {{admin.fname}} {{admin.lname}} a bien été créé
    </div>

</div>
